import axios from 'axios';
import { useEffect, useState } from 'react';

export default () => {
  const [postsList, setPostsList] = useState([]);
  const [title, setTitle] = useState('');
  const [body, setBody] = useState('');
  const [isEditMode, setIsEditMode] = useState(false);
  const [postId, setPostId] = useState(0);
  const [selectPostId, setSelectPostId] = useState(-1);
  const fetchData = () => {
    //code TO fetch the datat from the server

    axios.get('https://jsonplaceholder.typicode.com/posts').then((response) => {
      if (response && response.data) {
        setPostsList(response.data);
      }
    });
  };

  useEffect(() => {
    fetchData();
  }, []);

  const onTitleChange = (event) => {
    setTitle(event.target.value);
  };

  const onBodyChange = (event) => {
    setBody(event.target.value);
  };

  const onFormSubmit = (event) => {
    event.preventDefault();
    console.log(title, body);
    if (!isEditMode) {
      axios
        .post('https://jsonplaceholder.typicode.com/posts', {
          title,
          body,
          userId: 1,
        })
        .then((response) => {
          console.log(response);
          alert('Addded');
          //fetch the data again
          fetchData();
          //reset form
          setTitle('');
          setBody('');
        });
    } else {
      if (postId > 0) {
        axios
          .put('https://jsonplaceholder.typicode.com/posts/' + postId, {
            id: postId,
            title,
            body,
            userId: 1,
          })
          .then((response) => {
            if (response) {
              fetchData();
              alert('UPdated');
              onReset();
            }
          });
      }
    }
  };
  const onDeleteHandler = (id) => {
    axios
      .delete('https://jsonplaceholder.typicode.com/posts/' + id)
      .then((response) => {
        console.log(response.data);
        if (response) {
          fetchData();
          alert('Deleted');
        }
      });
  };
  const onEditHandler = (postObject) => {
    console.log(postObject);
    setIsEditMode(true);
    setTitle(postObject.title);
    setBody(postObject.body);
    setPostId(postObject.id);
    setSelectPostId(postObject.id);
  };
  const onReset = (event) => {
    if (event) {
      event.preventDefault();
    }
    setIsEditMode(false);
    setTitle('');
    setBody('');
  };
  return (
    <>
      <h2>Form HooksSample</h2>
      <h2>Post Form</h2>

      <form onSubmit={onFormSubmit}>
        <label>Title</label>
        <input name='title' value={title} onChange={onTitleChange}></input>

        <label>Body</label>
        <input name='body' value={body} onChange={onBodyChange}></input>
        <button type='submit'>{isEditMode ? 'Update' : 'Submit'}</button>
        {isEditMode && <button onClick={onReset}>Reset</button>}
      </form>

      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Title</th>
            <th>Body</th>
            <th>Options</th>
          </tr>
        </thead>
        <tbody>
          {postsList.map((post, index) => {
            return (
              <tr key={post.id}>
                <td>{post.id}</td>
                <td>
                  {selectPostId === post.id ? (
                    <input value={title} onChange={onTitleChange} />
                  ) : (
                    post.title
                  )}
                </td>
                <td>{post.body}</td>
                <td>
                  <button
                    onClick={() => {
                      onDeleteHandler(post.id);
                    }}
                  >
                    Delete
                  </button>
                  <button
                    onClick={() => {
                      onEditHandler(post);
                    }}
                  >
                    Edit
                  </button>
                </td>
              </tr>
            );
          })}
        </tbody>
      </table>
    </>
  );
};
