export default () =>{
    //const statearr=usestatte(0);
    const [value,setValue]=userState(0);
    const onClickHandler=()=>{
        setValue(value+1);

    };
    return(
        <>
        <h1>Value:{value}</h1>
        <button  onClick={onClickHandler}>Increement</button>
        </>
    );
};
